package day4_If_ElseConcepts;

public class Program07_PostIncrementConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = a++;
		System.out.println(a);
		System.out.println(b);
		
		// Note: a value will be given to b first then it will increment 
		// because it is a post increment
		// First assignment will happen then increment will happem
	}

}
